export function AdicionarPost() {
  const navigation = useNavigation();
  const {
    inputRemedio,
    setInputRemedio,
    inputDoenca,
    setInputDoenca,
    inputData,
    setInputData,
    flagEditando,
    idEdit,
    userInfo,
  } = useContext(RemedioContext);

  const criando_tratamento = async (enfermidade, periodo, droga) => {
    try {
      const id = await AsyncStorage.getItem('id');
      const response = await api.post(
        `/users/tratamento/${id}`,
        {enfermidade,
        periodo,
        droga}
      );

      console.log('Criou post tratamento', response.data);
    } catch (error) {
      console.log(error.message);
      console.log(error.response.data);
    }
  };
  const editando_tratamento = async (
    enfermidade,
    periodo,
    droga,
    cod_tratamento
  ) => {
    try {
      const id = await AsyncStorage.getItem('id');
      console.log(enfermidade)
      const response = await api.post(
        `/users/tratamento/edit/${id}`,
        {enfermidade,
        periodo,
        droga,
        cod_tratamento}
      );

      console.log('Dados editados', response.data);
    } catch (error) {
      console.log(error.message);
      console.log(error.response.data);
    }
  };

  return (
    <View style={styles.container}>
      <View style={styles.containerCorpo}>
        <View style={styles.containerDoencas}>
          <TextInput
            multiline
            numberOfLines={1}
            blurOnSubmit
            value={inputDoenca}
            onChangeText={setInputDoenca}
            style={styles.input}
            placeholder="Enfermidade"
          />
        </View>

        <View style={styles.containerDoencas}>
          <TextInput
            multiline
            numberOfLines={2}
            style={styles.input}
            value={inputRemedio}
            onChangeText={setInputRemedio}
            placeholder="Remédio(s)"
          />
        </View>

        <View style={styles.containerDoencas}>
          <TextInput
            multiline
            numberOfLines={2}
            blurOnSubmit
            style={styles.input}
            value={inputData}
            onChangeText={setInputData}
            placeholder="Data"
          />
        </View>
      </View>

      <View style={styles.footerBotao}>
        <TouchableOpacity
          onPress={() => {
            // Os valores dos inputs são enviados para as funções.
         
            flagEditando
              ? editando_tratamento(
                  inputDoenca,
                  inputData,
                  inputRemedio,
                  idEdit
                )
              : criando_tratamento(inputDoenca, inputData, inputRemedio);
            navigation.goBack();
          }}>
          <LinearGradient
            // Button Linear Gradient
            colors={['#CDE4AD', '#97D8AE', '#78D1D2']}
            style={styles.botaoPlus}>
            <Feather name="check-circle" size={40} color="white" />
          </LinearGradient>
        </TouchableOpacity>
      </View>
    </View>
  );
}

