import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, TouchableOpacity, View, Image, TextInput, ScrollView } from 'react-native';


import { useState } from 'react';

// let produtos = []
let produtos = [
  { nome: "Banana", preco: 111 },
  { nome: "limão", preco: 222 },
  { nome: "Melancia", preco: 333 },
  { nome: "Fiat uno", preco: 444 },
  { nome: "prego", preco: 555 },
  { nome: "Carinhosas", preco: 666 },
]
export default function App() {
  const [entradaNome, setEntradaNome] = useState('')
  const [entradaPreco, setEntradaPreco] = useState('')

  function cadastrar() {
    let produto = {
      nome: entradaNome,
      preco: Number(entradaPreco),
    }
    produtos.push(produto);
    console.log(produtos);
    setEntradaNome('');
    setEntradaPreco('');
  };
  function Apagar() {
    let produto=pesquisar()
    produtos.splice(produtos.indexOf(produto),1)
    setEntradaNome('');
    setEntradaPreco('');
  }
  function pesquisar() {
    for (let i = 0; i < produtos.length; i++) {
      if (entradaNome == produtos[i].nome) {
        setEntradaPreco(produtos[i].preco.toString())
        return produtos[i]
      }
    }

  };
  function Editar() {
    let produto=pesquisar()
    produto.preco=Number(entradaPreco)
    setEntradaNome('');
    setEntradaPreco('');
    console.log(produto)
  }
  function teste() {

    console.log(produtos);
    pesquisar().preco = 0;
    console.log(produtos);
  }

  return (

    <View style={styles.container}>

      <Text style={styles.teste}>Crud</Text>
      <TextInput

        value={entradaNome}
        onChangeText={setEntradaNome}
        style={styles.inputs}
        keyboardType='numeric'
        placeholder='nome...'
      />
      <TextInput
        value={entradaPreco}
        onChangeText={setEntradaPreco}
        style={styles.inputs}
        placeholder='Preço....'

      />
      <View style={styles.linha}>

      </View>

      <View style={styles.viemresultado}>
        {produtos.map((produto, index) => (
          <Text style={styles.telas} key={index}>{produto.nome} - R${produto.preco.toFixed(2)}

          </Text>
        ))}
      </View>

      <StatusBar style='auto' />
    </View>

  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#364ff2',
    alignItems: 'center',
    justifyContent: 'flex-star',

  },
  teste: {
    fontSize: 28,
  },
  inputs: {
    borderWidth: 1,
    width: 100,
    height: 45,
    marginVertical: 5,
    padding: 15,
    fontSize: 18,
    borderRadius: 10,
  },
  botao: {
    borderWidth: 1,
    backgroundColor: '#00008B',
    borderRadius: 10,

  },
  textBotao: {
    display: 'flex',
    width: 55,
    height: 50,
    textAlign: 'center',
    justifyContent: 'center',
    backgroundColor: '#F8F8FF',
    fontSize: 14,

    borderRadius: 10,
  },
  linha: {
    flexDirection: 'row',
    gap: 2,
  },
  telas: {
    fontSize: 25,
  },
});
