import React, { useState } from 'react';
import { View, TextInput, Button, StyleSheet, Alert, Image } from 'react-native';

const CadastroScreen = () => {
  const [campo1, setCampo1] = useState('');
  const [campo2, setCampo2] = useState('');
  const [campo3, setCampo3] = useState('');
  const [campo4, setCampo4] = useState('');
  const [campo5, setCampo5] = useState('');
  const [campoAtual, setCampoAtual] = useState(1);

  const cadastrar = () => {
    Alert.alert('Cadastro com sucesso', 'Seu cadastro foi realizado com sucesso!', [
      { text: 'OK', onPress: () => console.log('Alerta fechado') },
    ]);
  };

  const avancarParaProximoCampo = () => {
    if (campoAtual < 5) {
      setCampoAtual(campoAtual + 1);
    }
  };

  const voltarParaCampoAnterior = () => {
    if (campoAtual > 1) {
      setCampoAtual(campoAtual - 1);
    }
  };

  return (
    <View style={styles.container}>
      {/* Header */}
      <View style={styles.header}>
        <Image
          source={require('./caminho-da-sua-logo.png')} // Substitua pelo caminho da sua logo
          style={styles.logo}
        />
      </View>

      {/* Body */}
      <View style={styles.body}>
        {campoAtual === 1 && (
          <View>
            <TextInput
              value={campo1}
              onChangeText={setCampo1}
              placeholder="Nome"
              style={styles.input}
            />
            <Button title="Avançar" onPress={avancarParaProximoCampo} />
          </View>
        )}

        {/* Outros campos e botões aqui */}

        {campoAtual === 5 && (
          <View>
            <TextInput
              value={campo5}
              onChangeText={setCampo5}
              placeholder="CPF"
              style={styles.input}
            />
            <Button title="Cadastrar" onPress={cadastrar} />
          </View>
        )}
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  header: {
    alignItems: 'center',
  },
  logo: {
    width: 100, // Ajuste o tamanho da logo conforme necessário
    height: 100, // Ajuste o tamanho da logo conforme necessário
  },
  body: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: 'black'
  },
  input: {
    width: 200,
    height: 40,
    borderColor: 'gray',
    borderWidth: 1,
    marginBottom: 10,
    paddingHorizontal: 10,
  },
});

export default CadastroScreen;
