import React, { useState } from 'react';
import { View, Text, StyleSheet, TextInput, Button, Alert } from 'react-native';
import { createStackNavigator } from '@react-navigation/stack';

const Stack = createStackNavigator();

export default function Cadastro() {
  return (
    <Stack.Navigator>
      <Stack.Group>
        <Stack.Screen name="Email" component={Email} options={{ headerShown: false }} />
        <Stack.Screen name="Senha" component={Senha} options={{ headerShown: false }} />
        <Stack.Screen name="ConfirmarSenha" component={ConfirmarSenha} options={{ headerShown: false }} />
      </Stack.Group>
    </Stack.Navigator>
  );
}

// Função para renderizar o componente de Email
function Email({ navigation }) {
  const [nome, setNome] = useState('');

  const avancarParaProximaTela = () => {
    if (nome) {
      navigation.navigate('Senha');
    } else {
      Alert.alert('Erro', 'Por favor, preencha o campo Nome.');
    }
  };

  return (
    <View style={styles.container}>
      <Header />
      <View style={styles.body}>
        <TextInput
          style={styles.input}
          placeholder="Nome"
          onChangeText={(text) => setNome(text)}
          value={nome}
        />
        <Button
          title="OK"
          onPress={avancarParaProximaTela}
        />
      </View>
      <Footer />
    </View>
  );
}

// Função para renderizar o componente de Senha
function Senha({ navigation }) {
  const [email, setEmail] = useState('');

  const avancarParaProximaTela = () => {
    if (email) {
      navigation.navigate('ConfirmarSenha');
    } else {
      Alert.alert('Erro', 'Por favor, preencha o campo Email.');
    }
  };

  return (
    <View style={styles.container}>
      <Header />
      <View style={styles.body}>
        <TextInput
          style={styles.input}
          placeholder="Email"
          onChangeText={(text) => setEmail(text)}
          value={email}
        />
        <Button
          title="OK"
          onPress={avancarParaProximaTela}
        />
      </View>
      <Footer />
    </View>
  );
}

// Função para renderizar o componente de ConfirmarSenha
function ConfirmarSenha({ navigation }) {
  const [confirmarSenha, setConfirmarSenha] = useState('');

  const cadastrar = () => {
    if (confirmarSenha) {
      Alert.alert('Cadastro Concluído', 'Seu cadastro foi realizado com sucesso!');
    } else {
      Alert.alert('Erro', 'Por favor, preencha o campo Confirmar Senha.');
    }
  };

  return (
    <View style={styles.container}>
      <Header />
      <View style={styles.body}>
        <TextInput
          style={styles.input}
          placeholder="Confirmar Senha"
          onChangeText={(text) => setConfirmarSenha(text)}
          value={confirmarSenha}
        />
        <Button
          title="Cadastrar"
          onPress={cadastrar}
        />
      </View>
      <Footer />
    </View>
  );
}

// Componente para o cabeçalho
const Header = () => (
  <View style={styles.header}>
    <Text>Logo Aqui</Text>
  </View>
);

// Componente para o rodapé
const Footer = () => (
  <View style={styles.footer}>
    <Text>Desenvolvido por:</Text>
  </View>
);

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#97d8ae',
  },
  header: {
    backgroundColor: '#4CAF50',
    padding: 10,
    alignItems: 'center',
  },
  body: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  footer: {
    backgroundColor: '#4CAF50',
    padding: 10,
    alignItems: 'center',
  },
  input: {
    fontSize: 15,
    width: 220,
    height: 50,
    borderColor: 'black',
    borderWidth: 1,
    paddingLeft: 10,
    marginBottom: 10,
    borderRadius: 20,
  },
});
