import { MaterialCommunityIcons } from '@expo/vector-icons';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { useNavigation } from "@react-navigation/native";
import { createStackNavigator } from '@react-navigation/stack';
import { LinearGradient } from 'expo-linear-gradient';
import * as React from 'react';
import { ScrollView, StyleSheet, Text, TextInput, TouchableOpacity, View } from 'react-native';
import QRCode from 'react-native-qrcode-svg';

import { useFocusEffect } from '@react-navigation/native';

import Feather from "@expo/vector-icons/Feather";

import { useContext, useState } from 'react';
import { ContextInfo } from '../ContextInfo/contextinfo';
import { func } from 'prop-types';



const Tab = createBottomTabNavigator();
const Stack = createStackNavigator();

export default function Home() {
  const {
   esconderTabBar, setTabBar
  } = useContext(ContextInfo)

  return (

    <Tab.Navigator
      initialRouteName="StackFeed"
      screenOptions={{
        tabBarActiveTintColor: '#9cf0b9',
      }}
    >
      <Tab.Screen
        name="StackFeed"
        component={StackFeed}
        options={{
          tabBarLabel: 'Home',
          tabBarIcon: ({ color, size }) => (
            <MaterialCommunityIcons name="home" color={color} size={30} />
          ),
          headerShown: false,
        }}
      />

      <Tab.Screen
        name="Profile"
        component={Profile}
        options={{
          tabBarLabel: 'Profile',
          tabBarIcon: ({ color, size }) => (
            <MaterialCommunityIcons name="account" color={color} size={30} />
          ),
          headerShown: false,
        }}
      />

      {esconderTabBar &&(
        <Tab.Screen name="stack" component={EditUser}/>
      )}



    </Tab.Navigator>
  );
}



const styles = StyleSheet.create({
  geral: {
    width: '50%',
    height: '50%',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'space-between',
    gap: 10,
  },
});

function StackFeed() {

  return (
    <Stack.Navigator>
      <Stack.Screen name='EditUser' component={EditUser} options={{ title: 'Editar Dados',   tabBarVisible: false, 
            onBlur: () => console.log('Tela1 foi fechada') }} />

      <Stack.Screen name='Home' component={Feed} options={{ title: '' }} />
      <Stack.Screen name='QrCodeUser' component={QrCodeUser} options={{ title: '' }} />
    </Stack.Navigator>
  );
}

function Feed() {
  const navigation = useNavigation()
