import React from 'react';
import { useState } from 'react';
import { StyleSheet, View } from 'react-native';
import { Input, Text } from 'react-native-elements';
import { Button } from 'react-native-elements';
import Icon from 'react-native-vector-icons/FontAwesome';
import { createStackNavigator } from '@react-navigation/stack';
import { NavigationContainer } from '@react-navigation/native';
import login from './screens/login';
import principal from './screens/principal';
import cadastro  from './screens/cadastro';
 import kazadun from './screens/kazadun'
 import lotholorien from './screens/lotholorien';
 import condado from './screens/condado';
const Stack =createStackNavigator();

function MyStack() {
  return(
    <Stack.Navigator>
    <Stack.Screen name='kazadun' component={kazadun} /> 
    <Stack.Screen name='lotholorien' component={lotholorien} />
    <Stack.Screen name='condado' component={condado} />
    <Stack.Screen name='mordor' component={mordor} />
    <Stack.Screen name='rohan' component={rohan} />
    <Stack.Screen name='gondor' component={gondor} />
     <Stack.Screen name='principal' component={principal} />

    </Stack.Navigator>
  )
}

export default function App() {
 return(
  <NavigationContainer>
    <MyStack/>
  </NavigationContainer>
 )
  }
