import React, { useState } from 'react';
import { View, Button, Text, StyleSheet, Image, TextInput } from 'react-native';
import { createStackNavigator } from '@react-navigation/stack';
import { NavigationContainer } from '@react-navigation/native';

const Stack = createStackNavigator();

export default function Cadastro() {
  return (
    <Stack.Navigator>
      <Stack.Group>
        <Stack.Screen name="Email" component={Email} options={{ headerShown: false }} />
        <Stack.Screen name="Senha" component={Senha} options={{ headerShown: false }} />
        <Stack.Screen name="ConfirmarSenha" component={ConfirmarSenha} options={{ headerShown: false }} />
      </Stack.Group>
    </Stack.Navigator>
  );
}

function Header() {
  return (
    <View style={styles.header}>
      <Image
        source={require('caminho/para/sua/imagem.png')}
        style={{ width: 100, height: 100 }}
        resizeMode="contain"
      />
    </View>
  );
}

function Body({ children }) {
  return (
    <View style={styles.body}>
      {children}
    </View>
  );
}

function Email({ navigation }) {
  const [nome, setNome] = useState('');

  const avancarParaProximaTela = () => {
    if (nome) {
      navigation.navigate('Senha');
    } else {
      alert('Por favor, preencha o campo Nome.');
    }
  };

  return (
    <View style={styles.container}>
      <Header />
      <Body>
        <TextInput
          style={styles.input}
          placeholder="Nome"
          onChangeText={(text) => setNome(text)}
          value={nome}
        />
        <Button
          title="OK"
          onPress={avancarParaProximaTela}
        />
      </Body>
    </View>
  );
}

function Senha({ navigation }) {
  const [email, setEmail] = useState('');

  const avancarParaProximaTela = () => {
    if (email) {
      navigation.navigate('ConfirmarSenha');
    } else {
      alert('Por favor, preencha o campo Email.');
    }
  };

  return (
    <View style={styles.container}>
      <Header />
      <Body>
        <TextInput
          style={styles.input}
          placeholder="Email"
          onChangeText={(text) => setEmail(text)}
          value={email}
        />
        <Button
          title="OK"
          onPress={avancarParaProximaTela}
        />
      </Body>
    </View>
  );
}

function ConfirmarSenha({ navigation }) {
  const [confirmarSenha, setConfirmarSenha] = useState('');

  const cadastrar = () => {
    if (confirmarSenha) {
      alert('Cadastro Concluído');
    } else {
      alert('Por favor, preencha o campo Confirmar Senha.');
    }
  };

  return (
    <View style={styles.container}>
      <Header />
      <Body>
        <TextInput
          style={styles.input}
          placeholder="Confirmar Senha"
          onChangeText={(text) => setConfirmarSenha(text)}
          value={confirmarSenha}
        />
        <Button
          title="Cadastrar"
          onPress={cadastrar}
        />
      </Body>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#97d8ae',
  },
  header: {
    backgroundColor: '#4CAF50',
    padding: 10,
    alignItems: 'center',
  },
  body: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  input: {
    fontSize: 15,
    width: 220,
    height: 50,
    borderColor: 'black',
    borderWidth: 1,
    paddingLeft: 10,
    marginBottom: 10,
    borderRadius: 20,
  },
});
