import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text,  View, TouchableOpacity} from 'react-native';
import FontAwesome from "@expo/vector-icons/FontAwesome";
import Feather from "@expo/vector-icons/Feather";
import { useState } from 'react';






export default function App() {
  const [conteudoFeed, setConteudoFeed]=useState('BLABLABLA')

function teste(){
  alert('oi')
  setConteudoFeed('Boa! noob')
}
  return (

    <View style={styles.container}>
      <View style={styles.header}>
       <TouchableOpacity onPress={teste}>
         <FontAwesome
            name='home'
            size={45}
            color='#00FFFF'
          />
       </TouchableOpacity>
        <FontAwesome 
          name='user'
          size={45}
          color='#00FFFF'
        />
         <FontAwesome 
          name='bath'
          size={45}
          color='#00FFFF'
        />


      </View>

      <View style={styles.feed}>
        <Text style={{color:'#ff0000'}}>{conteudoFeed}</Text>
      </View>

      <View style={styles.footer}>
        <Text style={{color:'#ff0000'}}>Footer</Text>
      </View>

      <StatusBar style='auto' />
    </View>

  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#364ff2',
    alignItems: 'center',
    justifyContent: 'flex_star',

  },
  header:{
    backgroundColor:'#fff',
    flex:1,
    width:'100%',
    flexDirection:'row',
    alignItems:'center',
    gap:4,
    justifyContent:'space-between',

  },
  feed:{
    backgroundColor:'#fff000',
    flex:8,
    width:'100%'
  },
  footer:{
    backgroundColor:'#fff',
    flex:1,
    height:65,
    width:'100%',
  },


});
