import React, { useState } from 'react';
import { View, Button, Text, StyleSheet, Image, TextInput, TouchableOpacity } from 'react-native';
import { createStackNavigator } from '@react-navigation/stack';
import { NavigationContainer } from '@react-navigation/native';



const Stack = createStackNavigator();




const Header = () => (
    
  <View style={styles.header}>
    <View style={styles.containerLogo}>
  <Image
   source={require('/img/logo.png')}

    style={{ width: 300, height: 300 }}
    resizeMode="contain"
  />
</View>



    
  </View>
    
);

const Body = () => (
  <View style={styles.body} >
    <View style={styles.conteudoBody}>

    </View>
      
   
  </View>
);


function Email(a) {
  return function ({ navigation }) {

    return (
      <View  style={styles.body}>
        <View style={styles.conteudoInput}>
        <TextInput
          style={styles.input}
          placeholder="Email"
        // onChangeText={(text) => setNome(text)}
        // value={nome}
        />
        </View>
         <TouchableOpacity style={styles.botao} onPress={() => navigation.navigate(a)}>
        
        <Text>OK</Text>
               
        
         </TouchableOpacity>
      </View>
    );
  }
}

function Senha(a) {
  return function ({ navigation }) {
    return (
            
      <View  style={styles.body}>         
        <View style={styles.conteudoInput}>
        <TextInput
              style={styles.input}
              placeholder="Senha"
              // onChangeText={(text) => setNome(text)}
              // value={nome}
        />
        </View>
        <TouchableOpacity style={styles.botao} onPress={() => navigation.navigate(a)}>
        
       <Text>OK</Text>
              
       
        </TouchableOpacity>
      </View>
    );
  }
}

function ConfirmarSenha(a) {
  return function ({ navigation }) {
    return (
      <View  style={styles.body}>
        <View style={styles.conteudoInput}>
        <TextInput
          style={styles.input}
          placeholder="ConfirmarSenha"
        // onChangeText={(text) => setNome(text)}
        // value={nome}
        />
        </View>
         <TouchableOpacity style={styles.botao} onPress={() => navigation.navigate(a)}>
        
            <Text>OK</Text>
               
        
         </TouchableOpacity>
      </View>
    );
  }
}




export default function Cadastro() {
  return (

    <><Header />
    <Body></Body>
    
    <Stack.Navigator>

      <Stack.Group>

        <Stack.Screen name='Email' component={Email('Senha')} options={{ headerShown: false }} />
        <Stack.Screen name='Senha' component={Senha('Confirmar Senha')} options={{ headerShown: false }} />
        <Stack.Screen name='Confirmar Senha' component={ConfirmarSenha('')} options={{ headerShown: false }} />


      </Stack.Group>

    </Stack.Navigator></>
  )
}


const styles = StyleSheet.create({
  container: {
    
    backgroundColor: 'white',
  },
  header:{
    backgroundColor: 'black',
    height: '20%',
    
    flex: 4,
    
  },
  body:{
    width: '100%',
    justifyContent: 'center',
    alignContent: 'center',
    flex: 6,
    backgroundColor:'blue',
  },

  containerLogo:{

    justifyContent: 'center',
    alignItems: 'center',
    flex: 2,
  },
  conteudoBody:{
    backgroundColor:'black',
    flex: 2,

  },
  
  input: {
    fontSize: 15,
    width: 250,
    height: 50,
    borderColor: 'black',
    borderWidth: 1,
    paddingLeft: 10,
    marginBottom: 10,
    borderRadius: 20,
   

  },
  botao:{
    backgroundColor: 'blue',
    width: 150,
    height: 30,
    borderRadius: 10,
    justifyContent: 'center',
    alignContent: 'center',
   
  },
 


});
