import React from 'react';
import { View, Text, StyleSheet, Image, TextInput, TouchableOpacity } from 'react-native';
import { createStackNavigator } from '@react-navigation/stack';

const Stack = createStackNavigator();



function Email({ navigation }) {
  return (
    <View style={styles.body}>
      <View style={styles.container}>
        <TextInput
          style={styles.input}
          placeholder="Email"
        />
        <TouchableOpacity style={styles.botao} onPress={() => navigation.navigate('Senha')}>
          <Text>OK</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
}

function Senha({ navigation }) {
  return (
    <View style={styles.body}>
      <View style={styles.container}>
        <TextInput
          style={styles.input}
          placeholder="Senha"
        />
        <TouchableOpacity style={styles.botao} onPress={() => navigation.navigate('ConfirmarSenha')}>
          <Text>OK</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
}

function ConfirmarSenha({ navigation }) {
  return (
    <View style={styles.body}>
      <View style={styles.container}>
        <TextInput
          style={styles.input}
          placeholder="ConfirmarSenha"
        />
        <TouchableOpacity style={styles.botao} onPress={() => navigation.navigate('')}>
          <Text>OK</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
}

export default function Cadastro() {
  return (
    <>
      <Header />
      <Stack.Navigator>
        <Stack.Group>
          <Stack.Screen name='Email' component={Email} options={{ headerShown: false }} />
          <Stack.Screen name='Senha' component={Senha} options={{ headerShown: false }} />
          <Stack.Screen name='Confirmar Senha' component={ConfirmarSenha} options={{ headerShown: false }} />
        </Stack.Group>
      </Stack.Navigator>
    </>
  );
}

const styles = StyleSheet.create({
  header: {
    backgroundColor: 'black',
    height: '20%',
    flex: 1,
  },
  body: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: 'blue',
  },
  container: {
    width: '100%',
    justifyContent: 'center',
    alignItems: 'center',
  },
  input: {
    fontSize: 15,
    width: 250,
    height: 50,
    borderColor: 'black',
    borderWidth: 1,
    paddingLeft: 10,
    marginBottom: 10,
    borderRadius: 20,
    backgroundColor: 'white',
  },
  botao: {
    backgroundColor: 'red',
    width: 150,
    height: 50,
    borderRadius: 10,
    justifyContent: 'center',
    alignItems: 'center',
  },
});
