import { useState, useContext } from 'react';
import React from 'react';

export const ContextInfo = React.createContext()

export const ContextInfoProvider = ({ children }) => {
    //Erick
    const [inputEmail, setInputEmail] = useState('')
    const [inputSenha, setInputSenha] = useState('')
    const [inputConfirmaSenha, setInputConfirmaSenha] = useState('')
    //Alex
    const [inputNome, setInputNome] = useState('1')
    const [inputIdade, setInputIdade] = useState('2')
    const [inputAlergias, setInputAlergias] = useState('3')
    const [inputTelefone, setInputTelefone] = useState('4')
    const [inputContatoEmergencia, setInputContatoEmergencia] = useState('5')
    const [inputNtelefoneEmergencia, setNtelefoneEmergencia] = useState('6')
    const [inputNCep,setInputNcep]= useState('7')
    const [inputLogradouro,setInputLogradouro]= useState('8')
    const [inputNumeroCasa,setNumeroCasa]= useState('9')
    const [inputDoador, setInputDoador] =useState('0')
    const [inputSangue, setInputSangue]=useState('')
    const [inputOrgao, setInputOrgao] =useState('')

    const EditUser = [
        { Valor: inputNome, Settar: setInputNome },
        { Valor: inputIdade, Settar: setInputIdade },
        { Valor: inputEmail, Settar: setInputEmail },
        { Valor: inputTelefone, Settar: setInputTelefone },
        { Valor: inputAlergias, Settar: setInputAlergias },
        { Valor: inputContatoEmergencia, Settar: setInputContatoEmergencia },
        { Valor: inputNtelefoneEmergencia, Settar: setNtelefoneEmergencia },
        { Valor: inputNCep, Settar: setInputNcep },
        { Valor: inputLogradouro, Settar: setInputLogradouro },
        { Valor: inputNumeroCasa, Settar: setNumeroCasa },
        { Valor: inputDoador, Settar:  setInputDoador},
        { Valor: inputSangue, Settar: setInputSangue },
        { Valor: inputOrgao, Settar: setInputOrgao },
    ]

    return (
        <ContextInfo.Provider
            value={{
               //Ercik
                inputEmail, setInputEmail,
                inputSenha, setInputSenha,
                inputConfirmaSenha, setInputConfirmaSenha,
                //Alexs
                inputNome, setInputNome,
                inputIdade, setInputIdade,
                inputTelefone, setInputTelefone,
                inputAlergias, setInputAlergias,
                inputContatoEmergencia, setInputContatoEmergencia,
                inputNtelefoneEmergencia, setNtelefoneEmergencia,
                inputNCep,setInputNcep,
                inputLogradouro,setInputLogradouro,
                inputNumeroCasa,setNumeroCasa,
                inputDoador, setInputDoador,
                inputSangue, setInputSangue,
                inputOrgao, setInputOrgao,
                EditUser
                
            }}>
            {children}
        </ContextInfo.Provider>


    )

}
