import React from 'react';
import { useState } from 'react';
import { View, Image, StyleSheet, ScrollView } from 'react-native';
import { Input, Text } from 'react-native-elements';
import { TouchableOpacity } from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';




export default function Inicio({ navigation }) {

  const entrar = () => {
    navigation.navigate('Login')
    }
  

  const Cadastrar = () => {
    navigation.navigate('Cadastro')
  }

  return (

    <LinearGradient
      colors={['#CDE4AD', '#97D8AE', '#78D1D2']}
      //  background: linear-gradient(179.96deg, #CDE4AD 3.67%, #97D8AE 54.83%, #78D1D2 99.97%)
      style={styles.container}
    >
      <View style={styles.container}>
      {/* <Image style={styles.img}
            source={require('/img/Texto do seu parágrafo.jpg')}
           
          /> */}

<ScrollView
        horizontal
        pagingEnabled
        showsHorizontalScrollIndicator={false}
        contentContainerStyle={styles.scrollViewContent}
      >
        <View style={styles.page}>
          <View style={styles.containerLogo}>
            <Image
              source={require('../img/logoHelpX.png')}
              style={styles.logo}
              resizeMode="contain"
            />
          </View>
        </View>

        <View style={styles.page}>
          <View style={styles.h1}>
            <Text style={styles.titulo}>HelpX: Seu Passaporte de Informações Médicas</Text>
          </View>
        </View>

        <View style={styles.page}>
          <View style={styles.h1}>
            <Text style={styles.subtitulo}>
              Cadastre suas informações pessoais e médicas essenciais e crie seu QR Code de emergência.
            </Text>
          </View>
        </View>

        <View style={styles.page}>
          <View style={styles.h1}>
            <Text style={styles.descricao}>Esteja preparado para qualquer situação!</Text>
            <Text style={styles.descricao}>
              Basta escanear o seu QR Code para compartilhar rapidamente informações críticas com profissionais de saúde em caso de necessidade.
            </Text>
          </View>
        </View>
      </ScrollView>

  

        <View style={styles.containerBotao}>

          <TouchableOpacity
            style={styles.botao}
            onPress={entrar}
          >
            <Text style={styles.textoBotao}> Entrar </Text>

          </TouchableOpacity>
          <TouchableOpacity
            style={styles.botao}
            onPress={Cadastrar}
          >
            <Text style={styles.textoBotao}> Cadastrar </Text>

          </TouchableOpacity>
        </View>



      </View>
    </LinearGradient>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    // backgroundColor: "#C7FFCC",
    alignItems: "center",
    flexDirection: 'column',

    gap: 11,
  },
  logo:{
    marginTop: -100,
  },
  containerLogo: {
    justifyContent: 'center',
    alignItems: 'center',
    
  },
  tituloLogo:{
    width: '100%',
    height:'45%'
  },
  containerBotao: {
    width: '100%',
    height: '55%',
    justifyContent: 'space-evenly',
    alignItems: 'center'

  },
  botao: {
    width: '100%',
    width: 130,
    height: 30,
    borderWidth: 2,
    borderColor: 'white',
    borderRadius: 20,

  },
  textoBotao: {
    fontSize: 15,
    color: 'white',
    textAlign: 'center'

  },
  img: {
    width: 440,
    height: 140,
    marginTop: 25,
  },
  viemtexto: {
    width: 200,
    height: 100,
    justifyContent: 'center',
    alignItems: 'center'

  },
  textoTitulo: {
    fontSize: 50,
    color: 'white'

  },
  h1: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    paddingHorizontal: 20,
    marginTop: -70,
    
  },
  titulo: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
  },
  subtitulo: {
    fontSize: 18,
    textAlign: 'center',
    marginBottom: 10,
    fontWeight: 'bold',
  },
  descricao: {
    fontSize: 16,
    textAlign: 'center',
    fontWeight: 'bold',
  },

});
