import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, TouchableOpacity, View, Image, TextInput, ScrollView } from 'react-native';


import { useState } from 'react';

export default function App() {
  const [entrada, setEntrada] = useState()
  const [entrada2, setEntrada2] = useState()
  const [saida, setsaida] = useState()
  function clicando() {
    let n = Number(entrada)
    let n2 = Number(entrada2)
    let n3 = n + n2
    setsaida(n3)
  }
  function multiplo() {
    let n = Number(entrada)
    let n2 = Number(entrada2)
    let n3 = n * n2
    setsaida(n3)
  }

  return (

    <View style={styles.container}>
      <ScrollView >
      <View style={styles.container}>
        <Text>{saida}</Text>
        <TextInput

          style={styles.input}
          value={entrada}
          keyboardType='numeric'
          onChangeText={setEntrada}
        />
        <TextInput

          style={styles.input}
          value={entrada2}
          keyboardType='numeric'
          onChangeText={setEntrada2}
        />

        <TouchableOpacity style={styles.botao} onPress={clicando}>
          <Text style={styles.textocor}>somar</Text>
        </TouchableOpacity>
        <TouchableOpacity style={styles.botao} onPress={multiplo}>
          <Text style={styles.textocor}>x</Text>
        </TouchableOpacity>

        <StatusBar style='auto' />

        <View style={styles.quadro}>
          <Text>texto{saida}</Text>
        </View>

        <View style={styles.quadro}>
          <Text>texto</Text>
        </View>

        <View style={styles.quadro}>
          <Text>texto</Text>
        </View>

        <View style={styles.quadro}>
          <Text>texto</Text>
        </View>

        <View style={styles.quadro}>
          <Text>texto</Text>
        </View>
        </View>
      </ScrollView>
    </View>

  );
}

const styles = StyleSheet.create({
  container: {
    display: 'flex',
    backgroundColor: '#fff',
    gap: 15,
    alignItems: 'center',
    justifyContent: 'center',

  },

  input: {
    borderWidth: 1,
    width: 140,
    height: 45,

    borderRadius: 15,


  },
  botao: {
    width: 150,
    height: 50,
    marginRight: 240,
    backgroundColor: '#666666',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    borderRadius: 25,


  },
  quadro: {
    backgroundColor: 'green',
    height: 100,
    width:100,
    alignItems: 'center',
    justifyContent: 'center',
    borderRadius: 25,
    marginBottom:25,


  }



});
