import * as React from 'react';
import { Text, View, StyleSheet, ScrollView } from 'react-native';
import { MaterialCommunityIcons } from '@expo/vector-icons';
import { TouchableOpacity } from 'react-native';
import { useNavigation } from "@react-navigation/native";
import { createStackNavigator } from '@react-navigation/stack';

const Stack = createStackNavigator();

export function StackAdmin(){
   
    return (
        <Stack.Navigator initialRouteName='FeedAdmin'>
          <Stack.Screen name='FeedAdmin' component={FeedAdmin} 
           options={{
                        title: 'Feed',
                        headerStyle: {
                            backgroundColor: '#97D8AE',
                            borderColor: '#97D8AE',
                            borderWidth: 2,
                            borderBottomLeftRadius: 20,
                            borderBottomRightRadius: 20,

                        },
                        headerTintColor: '#fff',
                        headerTitleStyle: {
                            fontWeight: '500',
                            fontSize: 30,
                        },
                        headerTitleAlign: "center",
                    }}/>
          <Stack.Screen name='Relatorio' component={FeedAdmin} />
          <Stack.Screen name='Usuarios' component={QrCodeUser} />
          <Stack.Screen name='Suporte' component={QrCodeUser} />
        </Stack.Navigator>
      );

}

export default function FeedAdmin() {
    const navigation = useNavigation()
    return (
  
  
      <View style={{ flex: 1, justifyContent: 'flex-start', alignItems: 'center', backgroundColor: '#FFF' }}>
  
        <View style={feed.cima}>
  
          <TouchableOpacity
            onPress={() => navigation.navigate('StackTratamento')}
            style={{ width: '40%', height: '40%', backgroundColor: '#97D8AE', borderRadius: 10, justifyContent: 'center', alignItems: 'center', gap: 15, shadowColor: "#000", shadowOffset: { width: 0, height: 4 }, shadowOpacity: 0.30, shadowRadius: 4.65, elevation: 8, }}>
  
            <MaterialCommunityIcons name="graph" color={'white'} size={50} />
  
            <Text style={{ fontSize: 18, fontWeight: '800', color: '#3C8F5A' }}>
              Relatório
            </Text>
  
          </TouchableOpacity>
  
          <TouchableOpacity
            onPress={() => navigation.navigate('StackTratamento')}
            style={{ width: '40%', height: '40%', backgroundColor: '#97D8AE', borderRadius: 10, justifyContent: 'center', alignItems: 'center', gap: 15, shadowColor: "#000", shadowOffset: { width: 0, height: 4 }, shadowOpacity: 0.30, shadowRadius: 4.65, elevation: 8, }}>
  
            <MaterialCommunityIcons name="comment-account" color={'white'} size={50} />
  
            <Text style={{ fontSize: 18, fontWeight: '800', color: '#3C8F5A' }}>
              Suporte
            </Text>
  
          </TouchableOpacity>
  
  
          <TouchableOpacity
            onPress={() => navigation.navigate('StackTratamento')}
            style={{ width: '40%', height: '40%', backgroundColor: '#97D8AE', borderRadius: 10, justifyContent: 'center', alignItems: 'center', gap: 15, shadowColor: "#000", shadowOffset: { width: 0, height: 4 }, shadowOpacity: 0.30, shadowRadius: 4.65, elevation: 8, }}>
  
            <MaterialCommunityIcons name="account-multiple-outline" color={'white'} size={50} />
  
            <Text style={{ fontSize: 18, fontWeight: '800', color: '#3C8F5A' }}>
              Usuários
            </Text>
  
          </TouchableOpacity>
  
  
          <View style={feed.options}>
  
          </View>
  
        </View>
  
  
  
      </View>
    );
  }
  
  const feed = StyleSheet.create({
  
    cima: {
      height: '50%',
      width: '100%',
      justifyContent: 'center',
      alignContent: 'center',
      flexWrap: 'wrap',
      gap: 20,
      backgroundColor: 'transparent',
      borderBottomWidth: 2,
      borderColor: '#97D8AE',
    },
  
    options: {
      width: '40%',
      height: '40%',
      backgroundColor: '#cdcdcd',
      borderRadius: 10,
      justifyContent: 'center',
      alignItems: 'center',
  
    }
  })
