import React from 'react';
import { View, Text, StyleSheet, Image, TextInput, TouchableOpacity } from 'react-native';
import { createStackNavigator } from '@react-navigation/stack';
import { ContextInfo, ContextInfoProvider } from '../ContextInfo/contextinfo';
import { useContext } from 'react';


const Stack = createStackNavigator();


const Header = () => (
    
  <View style={styles.header}>
    <View style={styles.containerLogo}>
  <Image
   source={require('../img/logo.png')}

    style={{ width: 300, height: 200 }}
    resizeMode="contain"
  />
</View>
  </View>
);






export default function Cadastro() {
  return (
    <>
      <Header />
      <Stack.Navigator>
      <Stack.Group>

<Stack.Screen name='Email' component={Email('Senha')}/>
<Stack.Screen name='Senha' component={Senha('Confirmar Senha')} />
<Stack.Screen name='Confirmar Senha' component={ConfirmarSenha('Informacoes')}/>


</Stack.Group>
      </Stack.Navigator>
    </>
  );
}

const styles = StyleSheet.create({
  header: {
    backgroundColor: 'black',
    height: 200, 
    
  },
  body: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor:'#97d8ae',
    
  },
  containerLogo:{
    justifyContent: 'center',
    alignItems: 'center',

  },
  container: {
    width: '90%',
    height: '50%',
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#e0f3e9',
    borderRadius: '40px',
  },
  input: {
    
    fontSize: 15,
    width: '80%',

    height: 50,
    borderColor: 'black',
    borderWidth: 1,
    paddingLeft: 10,
    marginBottom: 10,
    borderRadius: 20,

    backgroundColor: 'white',
    
  },
 
  botao: {
    backgroundColor: '#78D1D2',
    width: 180,
    height: 50,
    borderRadius: 15,
    justifyContent: 'center',
    alignItems: 'center',
    
    
  },
  textoBotao:{
    color: 'white',
    fontFamily: 'Helvetica',
    fontWeight: 'bold',
  },
});
