import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View } from 'react-native';
import { TextInput } from 'react-native-web';


const Header = () => (
  <View style={styles.header}>
    <Text style={styles.textHeader}>Header

    </Text>
  </View>
);
const Body =() =>(
  <View>
  <View style={styles.body}>
    <Text style={styles.textBody}>BIN
    <TextInput 
    keyboardType='numeric'
    placeholder='BASE 2'/>
    </Text>
  </View>

  <View style={styles.body}>
    <Text style={styles.textBody}>OCT
    <TextInput 
    keyboardType='numeric'
    placeholder='BASE 8'/>
    </Text>
  </View>

  <View style={styles.body}>
    <Text style={styles.textBody}>DEC
    <TextInput 
    style={styles.convert}
    keyboardType='numeric'
    placeholder='BASE 10'/>
    </Text>
  </View>

  <View style={styles.body}>
    <Text style={styles.textBody}>HEX
    <TextInput 
    keyboardType='numeric'
    placeholder='BASE 16'/>
    </Text>
  </View>
  </View>
);
const Footer =() =>(
  <View style={styles.footer}>
    <Text style={styles.textFooter}>Footer

    </Text>
  </View>
);

export default function App() {
  return (
    <View style={styles.container}>

      <Header/>
      <Body />

      <Footer/>
      <Text></Text>
      <StatusBar style="auto" />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },

    header: {
    backgroundColor: 'red',
    flex: 1,
    width: '100%',
    alignItems: 'center',
    justifyContent: 'center',

    },
    body: {
    backgroundColor: 'gray',
    flex: 8,
    width: '100%',
    alignItems: 'center',
    justifyContent: 'center',
    },

    footer: {
    backgroundColor: 'green',
    flex: 1,
    width: '100%',
    alignItems: 'center',
    justifyContent: 'center',
    },

    textHeader:{
    fontSize: 25,
    },

    textBody:{
    fontSize: 25,


    },

    textFooter:{
    fontSize: 25,
    },
    });

