import React from 'react';
import { View, Text, StyleSheet, Image, TextInput, TouchableOpacity } from 'react-native';
import { createStackNavigator } from '@react-navigation/stack';

const Stack = createStackNavigator();


const Header = () => (
    
  <View style={styles.header}>
    <View style={styles.containerLogo}>
  <Image
   source={require('/img/logo.png')}

    style={{ width: 300, height: 200 }}
    resizeMode="contain"
  />
</View>
  </View>
);

function Email(a) {
  return function ({ navigation }) {

    return (
      <View  style={styles.body}>
        <View style={styles.container}>
        <TextInput
           style={[styles.input, { marginBottom: 20 }]}
          placeholder="Email"
        // onChangeText={(text) => setNome(text)}
        // value={nome}
        />
         <TouchableOpacity style={styles.botao} onPress={() => navigation.navigate(a)}>
        
        <Text>OK</Text>
               
        
         </TouchableOpacity>
        </View>
        
      </View>
    );
  }
}

function Senha(a) {
  return function ({ navigation }) {
    return (
            
      <View  style={styles.body}>         
        <View style={styles.container}>
        <TextInput
              style={[styles.input, { marginBottom: 20 }]}
              placeholder="Senha"
              secureTextEntry={true} 
              // onChangeText={(text) => setNome(text)}
              // value={nome}
        />
         <TouchableOpacity style={styles.botao} onPress={() => navigation.navigate(a)}>
        
        <Text>OK</Text>
               
        
         </TouchableOpacity>
        </View>
       
      </View>
    );
  }
}

function ConfirmarSenha(a) {
  return function ({ navigation }) {
    return (
      <View  style={styles.body}>
        <View style={styles.container}>
        <TextInput
           style={[styles.input, { marginBottom: 20 }]}
          placeholder="ConfirmarSenha"
          secureTextEntry={true}
        // onChangeText={(text) => setNome(text)}
        // value={nome}
        />
         <TouchableOpacity style={styles.botao} onPress={() => navigation.navigate()}>
        
        <Text>OK</Text>
           
    
     </TouchableOpacity>
        </View>
        
      </View>
    );
  }
}

export default function Cadastro() {
  const [cadastroConcluido, setCadastroConcluido] = useState(false);

  return (
    <>
      <Header />
      <Stack.Navigator>
      <Stack.Group>

<Stack.Screen name='Email' component={Email('Senha')} options={{ headerShown: false }} />
<Stack.Screen name='Senha' component={Senha('Confirmar Senha')} options={{ headerShown: false }} />
<Stack.Screen name='Confirmar Senha' component={ConfirmarSenha('')} options={{ headerShown: false }} />


</Stack.Group>
      </Stack.Navigator>
      {cadastroConcluido && <Text>Cadastro conclu√≠do com sucesso!</Text>}
    </>
  );
}

const styles = StyleSheet.create({
  header: {
    backgroundColor: 'black',
    height: 200, // Ajuste a altura do header aqui (por exemplo, 100)
    
  },
  body: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor:'#97d8ae',
  },
  containerLogo:{
    justifyContent: 'center',
    alignItems: 'center',
  },
  container: {
    width: '80%',
    height: '50%',
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#e0f3e9',
    borderRadius: '40px',
  },
  input: {
    
    fontSize: 15,
    width: '75%',
    height: 50,
    borderColor: 'black',
    borderWidth: 1,
    paddingLeft: 10,
    marginBottom: 10,
    borderRadius: 20,
    backgroundColor: 'white',
  },
  botao: {
    backgroundColor: 'red',
    width: 150,
    height: 50,
    borderRadius: 15,
    justifyContent: 'center',
    alignItems: 'center',
    
    
  },
});
