        <Button  style={styles.botaoDois}  title="Finalizar Cadastro" onPress={() => alert('Cadastro concluído')} />
      )}
 
      
        
     
    </View>
  );

  const Footer = () => (
  <Text>Desenvolvido por:</Text>
  );



const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#97d8ae',
  },
  header: {
    backgroundColor: '#4CAF50',
    padding: 10,
    alignItems: 'center',
  },
  headerText: {
    color: 'white',
    fontSize: 24,
  },
  body: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  footer: {
    backgroundColor: '#4CAF50',
    padding: 10,
    alignItems: 'center',
  },
  input: {
    fontSize: 15,
    width: 220,
    height: 50,
    borderColor: 'black',
    borderWidth: 1,
    paddingLeft: 10,
