import { useState, useContext } from 'react';
import React from 'react';

export const RemedioContext = React.createContext()

export const RemedioContextProvider = ({children}) => {
    const [inputRemedio, setInputRemedio] = useState('')
    const [inputDoenca, setInputDoenca] = useState('')
    const [inputData, setInputData] = useState('')
    const arrayTratamento = []
   
    function objTratamento(){
        let obj = {Enfermidade: inputDoenca,
            Remedio: inputRemedio,
            Data: inputData
        }
        arrayTratamento.push(obj)
        console.log(arrayTratamento)
    }  


    return (
        <RemedioContext.Provider
            value={{inputRemedio, setInputRemedio, inputDoenca, setInputDoenca, inputData, setInputData, arrayTratamento}}>
            {children}
        </RemedioContext.Provider>


    )

}
