// import { StatusBar } from 'expo-status-bar';
// import { StyleSheet, Text, TouchableOpacity, View, Image, TextInput, ScrollView } from 'react-native';
// //import { TextInput } from 'react-native-web';
// import FontAwesome from "@expo/vector-icons/FontAwesome";
// import Feather from "@expo/vector-icons/Feather";
// import { NavigationActionContainer, NavigationContainer } from '@react-navigation/native';

// import { createNativeStackNavigator } from '@react-navigation/native-stack';
// import { useState } from 'react';


export default function App() {
  const [imagemDado, setImagemdado] = useState(require('./imagens-dado/bilbo1.png'))
  const [noob, setnoob] = useState()
  const [novo, setnovo] = useState()
  function voltar() {
    setImagemdado(require('./imagens-dado/valfenda.png'))
    setnoob("")
    setnovo("")
  }
  function clicando() {
    let n = Math.ceil(Math.random() * 6)

    switch (n) {

      case 1:

        setImagemdado(require('./imagens-dado/kazadum.png'))
        setnoob("Kazadum casa dos an천es cavaram fundo e acordaram algo terrivel")
        setnovo("Um balrog de Melkor ")
         
        break

      case 2: setImagemdado(require('./imagens-dado/lotholorien.png'))
        setnoob("Irei ate a dama da floresta adiquirir conhecimento")
        break

      case 3: setImagemdado(require('./imagens-dado/rohan.png'))
        setnoob("Lutarei 찾o lado dos senhores dos cavalos")
        break

      case 4: setImagemdado(require('./imagens-dado/gondor.png'))
        setnoob("Lutarei na cidade branca ")
        break

      case 5: setImagemdado(require('./imagens-dado/mordor.png'))
        setnoob("Vou destruir o Anel")
        break

      case 6: setImagemdado(require('./imagens-dado/condado.png'))
        setnoob("Vou voltar para o condado")
        break

    }
  }

  return (

    <View style={styles.container}>
      <Image
        source={imagemDado}
        style={styles.imagem}
      />
      <Text>{noob}{novo}</Text>
      <TouchableOpacity style={styles.botao} onPress={clicando}>
        <Text style={styles.textocor}>Para onde frodo</Text>
      </TouchableOpacity>

      <TouchableOpacity style={styles.botao} onPress={voltar}>
        <Text style={styles.textocor}>Voltar a valfenda</Text>
      </TouchableOpacity>
      <StatusBar style='auto' />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    gap: 5,
    alignItems: 'center',
    justifyContent: 'center',

  },
  textocor: {
    color: 'red',
    fontSize: 30,
    marginLeft: 24,
    
  },
  botao: {
    width: 180,
    height: 90,
    backgroundColor: '#666666',
    display: 'flex',
    
    
    borderRadius: 25,



  },
  imagem: {
    width: '100%',
    height: '50%',
  }

});
