import React, { useState } from 'react';
import { View, TextInput, Button, StyleSheet, Alert } from 'react-native';


const Header = () => {
  

  const CadastroScreen = () => {
    const [campo1, setCampo1] = useState('');
    const [campo2, setCampo2] = useState('');
    const [campo3, setCampo3] = useState('');
    const [campo4, setCampo4] = useState('');
    const [campo5, setCampo5] = useState('');
    const [campoAtual, setCampoAtual] = useState(1);
  
    const cadastrar = () => {
  
     Alert("Cadastro com sucesso")
    };
    
  
    const avancarParaProximoCampo = () => {
      if (campoAtual < 5) {
        setCampoAtual(campoAtual + 1);
      }
    };
  
    const voltarParaCampoAnterior = () => {
      if (campoAtual > 1) {
        setCampoAtual(campoAtual - 1);
      }
    };
  
    return (
      <View style={styles.container}>
        {campoAtual === 1 && (
          <View>
            <TextInput
              value={campo1}
              onChangeText={setCampo1}
              placeholder="Nome"
              style={styles.input}
            />
            <Button title="Avançar" onPress={avancarParaProximoCampo} />
          </View>
        )}
  
        {campoAtual === 2 && (
          <View>
            <TextInput
              value={campo2}
              onChangeText={setCampo2}
              placeholder="Email"
              style={styles.input}
            />
            <Button title="Voltar" onPress={voltarParaCampoAnterior} />
            <Button title="Avançar" onPress={avancarParaProximoCampo} />
          </View>
        )}
  
        {campoAtual === 3 && (
          <View>
            <TextInput
              value={campo3}
              onChangeText={setCampo3}
              placeholder="Senha"
              style={styles.input}
            />
            <Button title="Voltar" onPress={voltarParaCampoAnterior} />
            <Button title="Avançar" onPress={avancarParaProximoCampo} />
          </View>
        )}
  
        {campoAtual === 4 && (
          <View>
            <TextInput
              value={campo4}
              onChangeText={setCampo4}
              placeholder="Confirmar Senha"
              style={styles.input}
            />
            <Button title="Voltar" onPress={voltarParaCampoAnterior} />
            <Button title="Avançar" onPress={avancarParaProximoCampo} />
          </View>
        )}
  
          {campoAtual === 5 && (
            <View>
              <TextInput
                value={campo5}
                onChangeText={setCampo5}
                placeholder="CPF"
                style={styles.input}
              />
              <Button title="Cadastrar" onPress={cadastrar} />
            </View>
                )}
              </View>
    );
  };
};

const CadastroScreen = () => {
  const [campo1, setCampo1] = useState('');
  const [campo2, setCampo2] = useState('');
  const [campo3, setCampo3] = useState('');
  const [campo4, setCampo4] = useState('');
  const [campo5, setCampo5] = useState('');
  const [campoAtual, setCampoAtual] = useState(1);

  const cadastrar = () => {

   Alert("Cadastro com sucesso")
  };
  

  const avancarParaProximoCampo = () => {
    if (campoAtual < 5) {
      setCampoAtual(campoAtual + 1);
    }
  };

  const voltarParaCampoAnterior = () => {
    if (campoAtual > 1) {
      setCampoAtual(campoAtual - 1);
    }
  };

  return (
    <View style={styles.container}>
      {campoAtual === 1 && (
        <View>
          <TextInput
            value={campo1}
            onChangeText={setCampo1}
            placeholder="Nome"
            style={styles.input}
          />
          <Button title="Avançar" onPress={avancarParaProximoCampo} />
        </View>
      )}

      {campoAtual === 2 && (
        <View>
          <TextInput
            value={campo2}
            onChangeText={setCampo2}
            placeholder="Email"
            style={styles.input}
          />
          <Button title="Voltar" onPress={voltarParaCampoAnterior} />
          <Button title="Avançar" onPress={avancarParaProximoCampo} />
        </View>
      )}

      {campoAtual === 3 && (
        <View>
          <TextInput
            value={campo3}
            onChangeText={setCampo3}
            placeholder="Senha"
            style={styles.input}
          />
          <Button title="Voltar" onPress={voltarParaCampoAnterior} />
          <Button title="Avançar" onPress={avancarParaProximoCampo} />
        </View>
      )}

      {campoAtual === 4 && (
        <View>
          <TextInput
            value={campo4}
            onChangeText={setCampo4}
            placeholder="Confirmar Senha"
            style={styles.input}
          />
          <Button title="Voltar" onPress={voltarParaCampoAnterior} />
          <Button title="Avançar" onPress={avancarParaProximoCampo} />
        </View>
      )}

        {campoAtual === 5 && (
          <View>
            <TextInput
              value={campo5}
              onChangeText={setCampo5}
              placeholder="CPF"
              style={styles.input}
            />
            <Button title="Cadastrar" onPress={cadastrar} />
          </View>
              )}
            </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  input: {
    width: 200,
    height: 40,
    borderColor: 'gray',
    borderWidth: 1,
    marginBottom: 10,
    paddingHorizontal: 10,
  },
});

export default CadastroScreen;
