import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View } from 'react-native';
import { TextInput } from 'react-native-web';
import FontAwesome from "@expo/vector-icons/FontAwesome";
import Feather from "@expo/vector-icons/Feather";

const Header = () => (
  <View style={styles.header}>
    <View style={styles.superior}>
      <Text style={styles.textconversor}>Conversor de base</Text>
      <Text style={styles.textReset}>Reset</Text>
      <Feather
        name={'more-vertical'}
        size={22}
        color='#00FFFF'
      />
    </View>

    <View style={styles.inferior}>
      <Text style={styles.textBases}>Bases Comuns</Text>
      <Text style={styles.textTodasBases}>Todas as Bases</Text>
    </View>

  </View>
);
const Body = () => (
  <View style={styles.bodytotal}>
    <View style={styles.bodyIndividual}>
      <Text style={styles.textBody}>BIN
        <TextInput
          style={styles.inputs}
          keyboardType='numeric'
          placeholder='BASE 2' />
      </Text>
    </View>

    <View style={styles.bodyIndividual}>
      <Text style={styles.textBody}>OCT
        <TextInput
          style={styles.inputs}
          keyboardType='numeric'
          placeholder='BASE 8' />
      </Text>
    </View>

    <View style={styles.bodyIndividual}>
      <Text style={styles.textBody}>DEC
        <TextInput
          style={styles.inputs}
          keyboardType='numeric'
          placeholder='BASE 10' />
      </Text>
    </View>

    <View style={styles.bodyIndividual}>
      <Text style={styles.textBody}>HEX
        <TextInput
          style={styles.inputs}
          keyboardType='numeric'
          placeholder='BASE 16' />
      </Text>
    </View>
  </View>
);
const Footer = () => (
  <View style={styles.footer}>
    <Text style={styles.textFooter}>Footer

    </Text>
  </View>
);

export default function App() {
  return (
    <View style={styles.container}>

      <Header />
      <Body />

      <Footer />
      <Text></Text>
      <StatusBar style="auto" />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#000000',
    alignItems: 'center',
    justifyContent: 'center',
    gap: 15
  },

  header: {
    backgroundColor: 'blue',
    width: '100%',
    flex: 2,
    justifyContent: 'space-between',
  },

  superior: {
    Color: '#ffffff',
    flexDirection: 'row',
    justifyContent: 'space-around',
    alignItems:'center',
  },
  textconversor:{
    color:'#ffffff',
    fontSize:20,

  }, 
  textReset: {
    color: '#ffffff',
    fontSize:20,
  },
  inferior: {
    flexDirection: 'row',
    justifyContent: 'space-evenly',
    color: 'white',
  },
  textBases:{
    color: '#ffffff',
    fontSize:25,
  },
  textTodasBases:{
    color: '#ffffff',
    fontSize:25,
  },



  bodyIndividual: {
    backgroundColor: 'blue',
    width: 250,
    height: 55,
    alignItems: 'center',
    justifyContent: 'center',

  },
  bodytotal: {
    flex: 8,
    gap:4
  },
  textbodyIndividual: {
    fontSize: 25,
    backgroundColor: 'blue',


  },
  textBody: {
    alignItems: 'center',
    justifyContent: 'center',
  },
  inputs: {
    alignItems: 'center',
    backgroundColor: 'black',
    color: '#ffffff',
    alignContent: 'center',
    borderRadius: 5,
    height: 45,
    width: 200,
    textAlign: 'center',
    marginLeft: 15
  },


});

