      />

      <TouchableOpacity style={styles.botao} onPress={cadastrar}>
        <Text style={styles.textBotao}>
          Add
        </Text>

      </TouchableOpacity>
      <StatusBar style='auto' />
    </View>

  );
}

const styles = StyleSheet.create({
  container: {
