import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, TouchableOpacity, View, Image, TextInput, ScrollView } from 'react-native';


import { useState } from 'react';

export default function App() {
 const [entradaNome, setEntradaNome]=useState('')
 const [entradaPreco,setEntradaPreco]=useState('')



  return (

    <View style={styles.container}>
      <ScrollView >
      <View style={styles.container}>
        <Text>Crud</Text>
        <TextInput

         value={entradaNome}
         onChangeText={setEntradaNome}
         style={styles.inputs}
        />
       <TextInput
       value={entradaPreco}
       onChangeText={setEntradaPreco}
       style={styles.inputs}
       />

        <StatusBar style='auto' />

        </View>
      </ScrollView>
    </View>

  );
  }

const styles = StyleSheet.create({
  container: {
    display: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',

  },

  inputs: {
    borderWidth: 1,
    width: 140,
    height: 45,
    borderRadius: 15,


  },

});
