import * as React from 'react';
import {
  Text,
  View,
  StyleSheet,
  ScrollView,
  TextInput,
  Modal,
  Alert,
} from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { MaterialCommunityIcons } from '@expo/vector-icons';
import { TouchableOpacity } from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';
import { useNavigation } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import QRCode from 'react-native-qrcode-svg';
import { ContextInfo } from '../ContextInfo/contextinfo';
import { useState, useRef, useContext, useEffect, Suspense } from 'react';
import Feather from '@expo/vector-icons/Feather';
import ViewShot from 'react-native-view-shot';
import * as MediaLibrary from 'expo-media-library';
import { Picker } from '@react-native-picker/picker';
import { RadioButton } from 'react-native-paper';
import api from '../Api_gerenciamento';
import AsyncStorage from '@react-native-async-storage/async-storage';


const Tab = createBottomTabNavigator();
const Stack = createStackNavigator();
let userAllInfo = []

export default function Home() {
  const navigation = useNavigation();
  const { idGuardado } = useContext(ContextInfo);
  useEffect(() => {
    const pegandoId = async () => {
      const idzinho = await AsyncStorage.getItem('id');
      // console.log('Entrei aqui na home para pegar o id', idzinho);
      idGuardado.push(idzinho);
      try {
        const response = await api.get(`/users/logged/${idzinho}`);
        userAllInfo.push(response.data)
        console.log('Dados do usuario na home', userAllInfo);
      } catch (error) {
        console.log(error.message);
        // console.log(error.response.data)
      }
    };
    pegandoId();
  }, []);


  return (
    <Tab.Navigator
      initialRouteName="StackFeed"
      screenOptions={{
        tabBarActiveTintColor: '#9cf0b9',
      }}>
      <Tab.Screen
        name="StackFeed"
        component={StackFeed}
        options={{
          tabBarLabel: 'Home',
          tabBarIcon: ({ color, size }) => (
            <MaterialCommunityIcons name="home" color={color} size={50} />
          ),
          headerShown: false,
        }}
      />

      <Tab.Screen
        name="Profile"
        component={Profile}
        options={{
          tabBarLabel: 'Profile',
          tabBarIcon: ({ color, size }) => (
            <MaterialCommunityIcons name="account" color={color} size={50} />
          ),
          headerShown: false,
        }}
      />
    </Tab.Navigator>
  );
}

const styles = StyleSheet.create({
  geral: {
    width: '50%',
    height: '50%',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'space-between',
    gap: 10,
  },
});

function StackFeed() {
  return (
    <Stack.Navigator>
      <Stack.Screen
        name="Feed"
        component={Feed}
        options={{ headerShown: false }}
      />
      <Stack.Screen
        name="EditUser"
        component={EditUser}
        options={{
          title: 'Editar Dados',
          headerTitleAlign: 'center',
          headerBackTitle: 'Voltar',
          headerStyle: {
            backgroundColor: '#97D8AE',
          },
          headerTintColor: '#fff',
          headerTitleStyle: {
            fontWeight: 'bold',
            fontSize: 24,
          },
        }}
      />

      <Stack.Screen
        name="QrCodeUser"
        component={QrCodeUser}
        options={{
          title: 'Meu QRCode',
          headerTitleAlign: 'center',
          headerBackTitle: 'Voltar',
          headerStyle: {
            backgroundColor: '#97D8AE',
          },
          headerTintColor: '#fff',
          headerTitleStyle: {
            fontWeight: 'bold',
            fontSize: 24,
          },
        }}
      />
    </Stack.Navigator>
  );
}

function Feed() {
  const navigation = useNavigation();
  const { inputNome, setInputNome } = useContext(ContextInfo);
  return (
    <View
      style={{
        flex: 1,
        justifyContent: 'flex-start',
        alignItems: 'center',
        backgroundColor: '#FFF',
      }}>
      <View style={feed.cima}>
        <TouchableOpacity
          onPress={() => navigation.navigate('StackTratamento')}
          style={{
            width: '40%',
            height: '40%',
            backgroundColor: '#97D8AE',
            borderRadius: 10,
            justifyContent: 'center',
            alignItems: 'center',
            gap: 15,
            shadowColor: '#000',
            shadowOffset: { width: 0, height: 4 },
            shadowOpacity: 0.3,
            shadowRadius: 4.65,
            elevation: 8,
          }}>
          <MaterialCommunityIcons name="pill" color={'white'} size={50} />

          <Text style={{ fontSize: 18, fontWeight: '800', color: '#3C8F5A' }}>
            Tratamento
          </Text>
        </TouchableOpacity>

        <TouchableOpacity
          onPress={() => navigation.navigate('Chat', { name: { inputNome } })}
          style={{
            width: '40%',
            height: '40%',
            backgroundColor: '#97D8AE',
            borderRadius: 10,
            justifyContent: 'center',
            alignItems: 'center',
            gap: 15,
            shadowColor: '#000',
            shadowOffset: { width: 0, height: 4 },
            shadowOpacity: 0.3,
            shadowRadius: 4.65,
            elevation: 8,
          }}>
          <MaterialCommunityIcons
            name="comment-account"
            color={'white'}
            size={50}
          />

          <Text style={{ fontSize: 18, fontWeight: '800', color: '#3C8F5A' }}>
            Suporte
          </Text>
        </TouchableOpacity>

        <TouchableOpacity
          onPress={() => navigation.navigate('QrCodeUser')}
          style={{
            width: '40%',
            height: '40%',
            backgroundColor: '#97D8AE',
            borderRadius: 10,
            justifyContent: 'center',
            alignItems: 'center',
            gap: 15,
            shadowColor: '#000',
            shadowOffset: { width: 0, height: 4 },
            shadowOpacity: 0.3,
            shadowRadius: 4.65,
            elevation: 8,
          }}>
          <MaterialCommunityIcons name="qrcode" color={'white'} size={50} />

          <Text style={{ fontSize: 18, fontWeight: '800', color: '#3C8F5A' }}>
            QR Code
          </Text>
        </TouchableOpacity>

        <TouchableOpacity
          onPress={() => navigation.navigate('EditUser')}
          style={{
            width: '40%',
            height: '40%',
            backgroundColor: '#97D8AE',
            borderRadius: 10,
            justifyContent: 'center',
            alignItems: 'center',
            gap: 15,
            shadowColor: '#000',
            shadowOffset: { width: 0, height: 4 },
            shadowOpacity: 0.3,
            shadowRadius: 4.65,
            elevation: 8,
          }}>
          <MaterialCommunityIcons
            name="account-edit"
            color={'white'}
            size={50}
          />

          <Text style={{ fontSize: 18, fontWeight: '800', color: '#3C8F5A' }}>
            Editar Perfil
          </Text>
        </TouchableOpacity>
      </View>
    </View>
  );
}

const feed = StyleSheet.create({
  cima: {
    height: '50%',
    width: '100%',
    justifyContent: 'center',
    alignContent: 'center',
    flexWrap: 'wrap',
    gap: 20,
    backgroundColor: 'transparent',
    borderBottomWidth: 2,
    borderColor: '#97D8AE',
    marginTop: '5%',
  },

  options: {
    width: '40%',
    height: '40%',
    backgroundColor: '#cdcdcd',
    borderRadius: 10,
    justifyContent: 'center',
    alignItems: 'center',
  },
});

function Profile() {
  const navigation = useNavigation();
  const { flagAdm, setFlagAdm, setInputEmail, setInputSenha, idGuardado } =
    useContext(ContextInfo);
  const [modalVisible, setModalVisible] = useState(false); // MUDA VISIBILIDADE DO MODAL
  const userDadosRef = useRef(userDados);
  const [userDados, setUserDados] = useState();


  useEffect(() => {
    const pegandoId = async () => {

      try {
        const response = await api.get(`/users/logged/${userAllInfo[0].cod_usuario}`);
        userDadosRef.current = response.data; // Utilizando o useRef
        setUserDados(response.data);
      } catch (error) {
        console.log(error.message);
        // console.log(error.response.data)
      }
    };
    pegandoId();
  }, []);

  // Função para sair e voltar para a tela inicial
  const limparStorage = async () => {
    await AsyncStorage.setItem('id', '');
  };
  const sair = () => {
    if (flagAdm) {
      setFlagAdm(!flagAdm);
    }
    limparStorage();
    setInputEmail('');
    setInputSenha('');
    navigation.popToTop();
  };

  const excluir_conta = async () => {
    try {
      const idz = await AsyncStorage.getItem('id');
      const response = await api.delete(`users/delete/${idz}`);

      console.log('Conta excluída com sucesso', response.data);
      sair();
    } catch (error) {
      console.log(error);
      console.log('Erro ao tentar excluir conta ');
    }
  };

  const renderizarInfoUsuario = () => {
    const usuario = userDadosRef.current;

    return (
      <View style={profile.containerInfos}>
        <View style={profile.viewNameUser}>
          <Text style={{ fontSize: 24, fontWeight: 'bold', color: 'green', }}>
            {usuario.nome}
          </Text>
        </View>
        <View style={profile.infoView}>
          <View style={profile.infoUser}>
