import React from 'react';
import { useState } from 'react';
import { View, Image, StyleSheet, KeyboardAvoidingView } from 'react-native';
import { Input, Text } from 'react-native-elements';
import { TouchableOpacity } from 'react-native';
import { color } from 'react-native-elements/dist/helpers';
import { LinearGradient } from 'expo-linear-gradient';
import Feather from "@expo/vector-icons/Feather";
import { useNavigation } from "@react-navigation/native";



export default function Login({ navigation }) {
 
 
  const entrar = () => {
    // navigation.navigate('Chat', {name: name})
    navigation.navigate('Login')

  }

  return (

    <LinearGradient
      colors={['#CDE4AD', '#97D8AE', '#78D1D2']}
      //  background: linear-gradient(179.96deg, #CDE4AD 3.67%, #97D8AE 54.83%, #78D1D2 99.97%)
      style={styles.container}>

<View style={styles.containerLogo}>
    <View style={styles.containerLogo}>
      <Image
        source={require('../img/logoHelpX.png')}

        style={{ width: 300, height: 500,}}
        resizeMode="contain"
      />
    </View>
  </View>
 
  <View style={styles.h1}>
      <Text style={styles.title}>HelpX: Seu Passaporte de Informações Médicas</Text>
      <Text style={styles.subtitle}>
        Cadastre suas informações pessoais e médicas essenciais e crie seu QR Code de emergência.
      </Text>
      <Text style={styles.description}>
        Esteja preparado para qualquer situação - basta escanear o seu QR Code para compartilhar
        rapidamente informações críticas com profissionais de saúde em caso de necessidade.
      </Text>
    </View>

      <View>
        <TouchableOpacity onPress={() => navigation.navigate('Cadastro')} style={styles.VoltarCadastro}>
          <Text style={styles.textoVoltarCadastro}> Ainda não tem conta?</Text>
        </TouchableOpacity>
      </View>

    </LinearGradient >
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    // backgroundColor: "#C7FFCC",
    alignItems: "center",
    flexDirection: 'column',
    paddingTop: '20%'

  },
  containerLogo: {
    justifyContent: 'center',
    alignItems: 'center',
    
    marginBottom: 500,
    

  },
 
  containerInputs: {
    width: '75%',
    height: '54%',
    borderWidth: 1,
    borderRadius: 20,
    borderColor: "white",
    backgroundColor: 'white',
    opacity: 0.71,

  },
  AlturaElementosInput: {

    height: '33.33%'
  },
  inpt: {
    width: '100%',
    height: 65,
  },
  inputLabel: {
    width: "100%",
    height: 40,
    fontWeight: 400,
    fontSize: 20,
    alignItems: 'flex-start',
    justifyContent: 'center',
    color: 'black',
    paddingLeft: 10,
    paddingTop: 18,
 
  },
  botao: {
    width: '80%',
    height: '50%',
    backgroundColor: '#78D1D2',
    borderWidth: 2,
    borderColor: '#589BAA',
    // box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
    borderRadius: 18,
    justifyContent: 'center',
    alignItems: 'center',
    alignSelf: 'center',

  },

  textoBotao: {
    // paddingTop: 5,
    textAlign: 'center',
    alignItems: 'center',
    color: '#FFFFFF',
    fontSize: 23,
    fontWeight: 500,
    fontStyle: 'normal',
    color: 'white'

  },
  icon: {
    opacity: 0.71,
    alignSelf: 'center'
  },
  VoltarCadastro: {
    paddingTop: '20%',
    width: '100%',
    height: '5%',
  },
  textoVoltarCadastro: {
    fontSize: 20,
    fontWeight: 700,
    color: '#2E7A4D',
    // textDecorationLine: 'underline'

  },
  h1: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    paddingHorizontal: 20,
    
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
  },
  subtitle: {
    fontSize: 18,
    textAlign: 'center',
    marginBottom: 10,
  },
  description: {
    fontSize: 16,
    textAlign: 'center',
  },
});
