import React from 'react';
import { View, Image, StyleSheet, TouchableOpacity } from 'react-native';
import { Text } from 'react-native'; // Importe o Text a partir do react-native
import Feather from "@expo/vector-icons/Feather";
import { useNavigation } from "@react-navigation/native";
import { LinearGradient } from 'expo-linear-gradient';

export default function Login({ navigation }) {
  return (
    <LinearGradient
      colors={['#CDE4AD', '#97D8AE', '#78D1D2']}
      style={styles.container}
    >
      <View style={styles.containerLogo}>
        <Image
          source={require('../img/logoHelpX.png')}
          style={styles.logo}
          resizeMode="contain"
        />
      </View>

      <View style={styles.infoContainer}>
        <Text style={styles.title}>HelpX: Seu Passaporte de Informações Médicas</Text>
        <Text style={styles.subtitle}>
          Cadastre suas informações pessoais e médicas essenciais e crie seu QR Code de emergência.
        </Text>
        <Text style={styles.description}>
          Esteja preparado para qualquer situação - basta escanear o seu QR Code para compartilhar
          rapidamente informações críticas com profissionais de saúde em caso de necessidade.
        </Text>
      </View>

      <TouchableOpacity onPress={() => navigation.navigate('Cadastro')} style={styles.VoltarCadastro}>
        <Text style={styles.textoVoltarCadastro}> Ainda não tem uma conta?</Text>
      </TouchableOpacity>
    </LinearGradient>
  );
}




const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
    padding: 20,
  },
  containerLogo: {
    alignItems: 'center',
  },
  logo: {
    width: 300,
    height: 200,
  },
  infoContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
    textAlign: 'center',
  },
  subtitle: {
    fontSize: 18,
    textAlign: 'center',
    marginBottom: 10,
  },
  description: {
    fontSize: 16,
    textAlign: 'center',
  },
  VoltarCadastro: {
    paddingTop: 20,
  },
  textoVoltarCadastro: {
    fontSize: 20,
    fontWeight: 'bold',
    color: '#2E7A4D',
  },
});
