import { StyleSheet, Text, View, TouchableOpacity, ScrollView } from 'react-native';
import FontAwesome from "@expo/vector-icons/FontAwesome";
import Feather from "@expo/vector-icons/Feather";
import { useState } from 'react';






export default function App() {
  const [conteudoFeed, setConteudoFeed] = useState(<Feed />)

  function teste() {


  }
  return (

    <View style={styles.container}>
      <View style={styles.header}>
        <TouchableOpacity onPress={teste}>
          <FontAwesome
            name='home'
            size={45}
            color='#00FFFF'
          />
        </TouchableOpacity>

        <TouchableOpacity onPress={Feed}>
          <FontAwesome
            name='user'
            size={45}
            color='#00FFFF'
          />
        </TouchableOpacity>

        <FontAwesome
          name='bath'
          size={45}
          color='#00FFFF'
        />


      </View>

      <View style={styles.feed}>
        <ScrollView>
          {conteudoFeed}
        </ScrollView>

      </View>

      <View style={styles.footer}>
        <Text style={{ color: '#ff0000' }}>Footer</Text>
      </View>

      <StatusBar style='auto' />
    </View>

  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'flex_star',

  },
  header: {
    backgroundColor: '#fff',
    height: 65,
    width: '100%',
    flexDirection: 'row',
    alignItems: 'center',
    gap: 4,
    justifyContent: 'space-around',

  },
  feed: {
    backgroundColor: '#fff000',
    flex:1,
    width: '100%'
  },
  footer: {
    backgroundColor: '#fff',

    height: 65,
    width: '100%',
  },


});

function Feed() {
  return (
    <View>
      {/* <Text style={{color:'#364ff2', marginTop:20}}>
        Ola campones
      </Text> */}
      <Post texto={"Texto que eu comprei da shein e ficou parado em Curitiba"}/>

    </View>
  )
}

function Post(props) {
  return (
    <View style={{ alignItems: 'center', padding: 15, marginBottom: 25 }}>
      <FontAwesome
        name='user'
        size={145}
        color='#00FFFF'
      />
      <Text> {props.texto}
      </Text>
    </View>
  )
}
