import React, { useState } from 'react';
import { View, Button, Text, StyleSheet,Image, TextInput } from 'react-native';
import { createStackNavigator } from '@react-navigation/stack';
import { NavigationContainer } from '@react-navigation/native';



const Stack = createStackNavigator();



export default function Cadastro() {
  return(
    
    <Stack.Navigator>

      <Stack.Group>
      
      <Stack.Screen name='Email' component={Email('Senha')} options={{ headerShown: false }}/>
      <Stack.Screen name='Senha' component={Senha('Confirmar Senha')} options={{ headerShown: false }}/>
      <Stack.Screen name='Confirmar Senha' component={ConfirmarSenha('')} options={{ headerShown: false }}/>
 
      
      </Stack.Group>

    </Stack.Navigator>
  )
}

 
    
      
  

 


const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#97d8ae',
  },
  header: {
    backgroundColor: '#4CAF50',
    padding: 10,
    alignItems: 'center',
  },
  headerText: {
    color: 'white',
    fontSize: 24,
  },
  body: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  footer: {
    backgroundColor: '#4CAF50',
    padding: 10,
    alignItems: 'center',
  },
  input: {
    fontSize: 15,
    width: 220,
    height: 50,
    borderColor: 'black',
    borderWidth: 1,
    paddingLeft: 10,
    marginBottom: 10,
    borderRadius: 20,
  },
  containerLogo:{
    flex: 2,
    justifyContent: 'center',
    alignItems: 'center',
  },

});

function Email(a){
  return function ({ navigation }){
 
    return(
  <View>
    
   <TextInput
  style={styles.input}
  placeholder="Nome"
  // onChangeText={(text) => setNome(text)
  // value={nome}
/>
<Button
            title={`OK`}
            onPress={() => navigation.navigate(a)}
          />

</View>
);
  }
}

function Senha(a){
  return function ({ navigation }){
  return(
  <View>
    
   <TextInput
  style={styles.input}
  placeholder="Email"
  // onChangeText={(text) => setNome(text)}
  // value={nome}
/>
<Button
            title={`OK`}
            onPress={() => navigation.navigate(a)}
          />

</View>
);
  }
}

function ConfirmarSenha(a){
  return function ({ navigation }){
  return(
  <View>
    
   <TextInput
  style={styles.input}
  placeholder="Confirmar Senha"
  // onChangeText={(text) => setNome(text)}
  // value={nome}
/>
<Button
            title={`OK`}
            onPress={() => navigation.navigate(a)}
          />

</View>
);
  }
}
