import React from 'react';
import { View, Button, Text, StyleSheet, Image, TextInput } from 'react-native';
import { createStackNavigator } from '@react-navigation/stack';
import { NavigationContainer } from '@react-navigation/native';

const Stack = createStackNavigator();

export default function Cadastro() {
  return (
    <NavigationContainer>
      <Stack.Navigator
        screenOptions={{
          header: () => <Header />,
        }}
      >
        <Stack.Screen name='Email' component={Email} />
        <Stack.Screen name='Senha' component={Senha} />
        <Stack.Screen name='ConfirmarSenha' component={ConfirmarSenha} />
      </Stack.Navigator>
    </NavigationContainer>
  );
}

function Header() {
  return (
    <View style={styles.header}>
      <Image
        source={require('caminho/para/sua/imagem.png')}
        style={{ width: 100, height: 100 }}
        resizeMode="contain"
      />
    </View>
  );
}

function Body({ children }) {
  return (
    <View style={styles.body}>
      {children}
    </View>
  );
}

function Email({ navigation }) {
  return (
    <Body>
      <TextInput
        style={styles.input}
        placeholder="Nome"
      />
      <Button
        title={`OK`}
        onPress={() => navigation.navigate('Senha')}
      />
    </Body>
  );
}

function Senha({ navigation }) {
  return (
    <Body>
      <TextInput
        style={styles.input}
        placeholder="Email"
      />
      <Button
        title={`OK`}
        onPress={() => navigation.navigate('ConfirmarSenha')}
      />
    </Body>
  );
}

function ConfirmarSenha({ navigation }) {
  return (
    <Body>
      <TextInput
        style={styles.input}
        placeholder="Confirmar Senha"
      />
      <Button
        title={`OK`}
        onPress={() => alert("Cadastro Concluído")}
      />
    </Body>
  );
}

const styles = StyleSheet.create({
  header: {
    backgroundColor: '#4CAF50',
    padding: 10,
    alignItems: 'center',
  },
  body: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  input: {
    fontSize: 15,
    width: 220,
    height: 50,
    borderColor: 'black',
    borderWidth: 1,
    paddingLeft: 10,
    marginBottom: 10,
    borderRadius: 20,
  },
});
