import { useState } from 'react';

let postagens = [
  {imagem: 'youtube', texto: 'Ae galera, saiu video novo no canal. Passa l√° pra ver eu destruindo no portugol!üîÆüì∫ \nyuoutube.com/devManoJuca \n#fullStackPortugol #portugolehprogramacao #respeitaosdev #tmj'},
  {imagem: 'arrow-up-right', texto: 'Cada linha de c√≥digo √© uma oportunidade para transformar l√≥gica em realidade. Codando para criar o futuro. üíª‚ú® #DesenvolvimentoInovador'},
