  import { MaterialCommunityIcons } from '@expo/vector-icons';
  import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
  import { useNavigation, useFocusEffect} from "@react-navigation/native";
  import { createStackNavigator } from '@react-navigation/stack';
  import { LinearGradient } from 'expo-linear-gradient';
  import * as React from 'react';
  import { ScrollView, StyleSheet, Text, TextInput, TouchableOpacity, View } from 'react-native';
  import QRCode from 'react-native-qrcode-svg';


  import Feather from "@expo/vector-icons/Feather";

  import { useContext, useState } from 'react';
  import { ContextInfo } from '../ContextInfo/contextinfo';




  const Tab = createBottomTabNavigator();
  const Stack = createStackNavigator();

  export default function Home() {
    
    return (

      <Tab.Navigator
        initialRouteName="StackFeed"
        screenOptions={{
          tabBarActiveTintColor: '#9cf0b9',
        }}
        
      >
        <Tab.Screen
          name="StackFeed"
          component={StackFeed}
          options={{
            tabBarLabel: 'Home',
            tabBarIcon: ({ color, size }) => (
              <MaterialCommunityIcons name="home" color={color} size={30} />
            ),
            headerShown: false, 
          }}
        />

        <Tab.Screen
          name="Profile"
          component={Profile}
          options={{
            tabBarLabel: 'Profile',
            tabBarIcon: ({ color, size }) => (
              <MaterialCommunityIcons name="account" color={color} size={30} />
            ),
            headerShown: false,
            
          }}
        />

      </Tab.Navigator>
    );
  }



  const styles = StyleSheet.create({
    geral: {
      width: '50%',
      height: '50%',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'space-between',
      gap: 10,
    },
  });

  function StackFeed() {

    return (
      <Stack.Navigator>
        <Stack.Screen name='Home' component={Feed} options= {{headerShown: false }}  />
        <Stack.Screen name='EditUser' component={EditUser} options= {{title: 'Editar Dados', headerBackTitle: 'Voltar', }} />
        <Stack.Screen name='QrCodeUser' component={QrCodeUser}  />
      </Stack.Navigator>
    );
  }

  function Feed() {
    const navigation = useNavigation()
    const { inputNome, setInputNome } = useContext(ContextInfo)

    return (


      <View style={{ flex: 1, justifyContent: 'flex-start', alignItems: 'center', backgroundColor: '#FFF' }}>

        <View style={feed.cima}>

          <TouchableOpacity
            onPress={() => navigation.navigate('StackTratamento')}
            style={{ width: '40%', height: '40%', backgroundColor: '#97D8AE', borderRadius: 10, justifyContent: 'center', alignItems: 'center', gap: 15, shadowColor: "#000", shadowOffset: { width: 0, height: 4 }, shadowOpacity: 0.30, shadowRadius: 4.65, elevation: 8, }}>

