import React, { useState } from 'react';
import { View, Button, Text, StyleSheet,Image, TextInput } from 'react-native';
import { createStackNavigator } from '@react-navigation/stack';
import { NavigationContainer } from '@react-navigation/native';



const Stack = createStackNavigator();



function Cadastro() {
  return(
    <Stack.Navigator>

      <Stack.Group>
      <Stack.Screen name='Nome' component={Nome} options={{ headerShown: false }}/>
      <Stack.Screen name='Email' component={Email} options={{ headerShown: false }}/>
      <Stack.Screen name='Home' component={Home} />
      <Stack.Screen name='Login' component={Login} options={{ headerShown: false }}/>
      <Stack.Screen name='Inicio' component={Inicio} />
      
      </Stack.Group>

    </Stack.Navigator>
  )
}

 
  const Header = () => (
    
    <View style={styles.header}>
      <View style={styles.containerLogo} >
          <Image
            source={require('/img/logopronto.png')}
            style={{ width: 100, height: 100 }}
            resizeMode="contain"
          />
      </View>


      
    </View>
      
  );

  const Body = () => (
    <View style={styles.body}>
    
     
     
    </View>
  );

  const Footer = () => (
  <Text>Desenvolvido por:</Text>
  );



const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#97d8ae',
  },
  header: {
    backgroundColor: '#4CAF50',
    padding: 10,
    alignItems: 'center',
  },
  headerText: {
    color: 'white',
    fontSize: 24,
  },
  body: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  footer: {
    backgroundColor: '#4CAF50',
    padding: 10,
    alignItems: 'center',
  },
  input: {
    fontSize: 15,
    width: 220,
    height: 50,
    borderColor: 'black',
    borderWidth: 1,
    paddingLeft: 10,
    marginBottom: 10,
    borderRadius: 20,
  },
  containerLogo:{
    flex: 2,
    justifyContent: 'center',
    alignItems: 'center',
  },

});

export default Cadastro;

function Nome(){
 return(
  <View>
    
   <TextInput
  style={styles.input}
  placeholder="Nome"
  onChangeText={(text) => setNome(text)}
  value={nome}
/>
<Button
            title={`OK`}
            onPress={() => navigation.navigate('Email')}
          />

</View>
);
}

function Email(){
 return(
  <View>
    
   <TextInput
  style={styles.input}
  placeholder="Email"
  onChangeText={(text) => setNome(text)}
  // value={nome}
/>
<Button
            title={`OK`}
            onPress={() => navigation.navigate('Email')}
          />

</View>
);
}
