import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View } from 'react-native';
import { TextInput } from 'react-native-web';
import FontAwesome from "@expo/vector-icons/FontAwesome";
import Feather from "@expo/vector-icons/Feather";
import { NavigationActionContainer, NavigationContainer } from '@react-navigation/native';
import { teste } from './mimo/teste.js'
import { createNativeStackNavigator } from '@react-navigation/native-stack';


const Header = () => (
  <View style={stylesHeader.header}>
    <View style={stylesHeader.superior}>
      <Text style={stylesHeader.textconversor}>Conversor de base</Text>
      <Text style={stylesHeader.textReset}>RESET</Text>
      <Feather
        name={'more-vertical'}
        size={22}
        color='#00FFFF'
      />
    </View>

    <View style={stylesHeader.inferior}>
      <Text style={stylesHeader.textBases}>Bases Comuns</Text>
      <Text style={stylesHeader.textTodasBases}>Todas as Bases</Text>
    </View>

  </View>
);

const stylesHeader = StyleSheet.create({
  header: {
    backgroundColor: '#2470f4',
    width: '100%',
    flex: 2,
    justifyContent: 'space-between',
    padding: 15
  },

  superior: {
    Color: '#ffffff',
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
  },
  textconversor: {
    color: '#ffffff',
    fontSize: 25,
    paddingHorizontal: 40

  },
  textReset: {
    color: '#ffffff',
    fontSize: 20,

  },
  inferior: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    color: 'white',
  },
  textBases: {
    color: '#ffffff',
    fontSize: 25,
  },
  textTodasBases: {
    color: '#ffffff',
    fontSize: 25,
  },
})

const Body = () => (
  <View style={stylesBody.bodytotal}>
    <View style={stylesBody.bodyIndividual}>
      <Text style={stylesBody.textBody}>BIN
        <TextInput
          style={stylesBody.inputs}
          keyboardType='numeric'
          placeholder='BASE 2' />
      </Text>
    </View>

    <View style={stylesBody.bodyIndividual}>
      <Text style={stylesBody.textBody}>OCT
        <TextInput
          style={stylesBody.inputs}
          keyboardType='numeric'
          placeholder='BASE 8' />
      </Text>
    </View>

    <View style={stylesBody.bodyIndividual}>
      <Text style={stylesBody.textBody}>DEC
        <TextInput
          style={stylesBody.inputs}
          keyboardType='numeric'
          placeholder='BASE 10' />
      </Text>
    </View>

    <View style={stylesBody.bodyIndividual}>
      <Text style={stylesBody.textBody}>HEX
        <TextInput
          style={stylesBody.inputs}
          keyboardType='numeric'
          placeholder='BASE 16' />
      </Text>
    </View>
  </View>
);

const stylesBody = StyleSheet.create({
  bodyIndividual: {
    backgroundColor: '#2470f4',
    width: 290,
    height: 55,
    alignItems: 'center',
    justifyContent: 'center',
    borderTopLeftRadius: 20,
    borderBottomLeftRadius: 20
  },
  bodytotal: {
    flex: 8,
    gap: 4
  },
  textbodyIndividual: {
    fontSize: 25,
    backgroundColor: 'blue',


  },
  textBody: {
    alignItems: 'center',
    justifyContent: 'center',
    color: 'white',
    fontSize: 20,
  },
  inputs: {
    alignItems: 'center',
    backgroundColor: '#262026',
    color: '#ffffff',
    alignContent: 'center',
    borderRadius: 5,
    height: 45,
    width: 200,
    textAlign: 'center',
    marginLeft: 15
  },

});
const Footer = () => (
  <View style={styles.footer}>
    <Text style={styles.textFooter}>SHEIN

    </Text>
  </View>
);

const Stack = createNativeStackNavigator();
export default function App() {
  return (
    <View style={styles.container}>



      <StatusBar style="auto" />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#262026',
    alignItems: 'center',
    justifyContent: 'center',
    gap: 15
  },
  footer: {
    backgroundColor: '#fff',
    width: '80%',
    height: 60,

    justifyContent: 'center'
  },
  textFooter: {
    fontSize: 35,
    margin: 15,
  }

});

